<script id="comments-render" type="text/html">
    {{each comments.items as comment i}}
    <div id="comment-{{comment['id']}}" class="comment-block clearfix floor-{{comment['rank']}} ">
        <div class="avatars">
            {{if comment['anonymous']}}
            <img src="http://static.qiushibaike.com/images/thumb/anony.png"/>
            {{ else }}
            <a href="/users/{{comment['uid']}}/" target="_blank"><img src="{{parseAvatar(comment['uid'],comment['avatar_file_name'])}}"/></a>
            {{ /if }}
        </div>
        <div class="replay">
            {{ if comment['anonymous']  }}
            <span class="anomynous">匿名人士</span>
            {{ else }}
            <a href="/users/{{comment['uid']}}/" {{ if comment['role_id'] }}class="manager"{{ else }}class="userlogin"{{ /if }} target="_blank">{{comment['login']}}</a>
            {{ /if }}
              <span class="body">
               {{ if comment['status'] == 'publish' }}
                {{ #content( comment['content'] ) }}
                  {{ else }}
                         发言被屏蔽
                  {{ /if }}
               </span>
        </div>
        <div class="report">
            {{comment['rank']}}
        </div>
    </div>
    {{/each}}
</script>

</div>



